import React, { useState } from "react";
import casual from "../asset/casual/Gradient Graphic T-shirt.png";
import "./css/Main.css";

const products = [
    { id: 1, name: "Gradient Graphic T-shirt", price: 145, rating: 3.6 },
    { id: 2, name: "Polo with Tipping Details", price: 180, rating: 4.5 },
    { id: 3, name: "Black Striped T-shirt", price: 120, oldPrice: 150, discount: 20, rating: 3.6 },
    { id: 4, name: "Skinny Fit Jeans", price: 240, oldPrice: 260, discount: 5, rating: 3.6 },
    { id: 5, name: "Checkered Shirt", price: 180, rating: 4.6 },
    { id: 6, name: "Sleeve Striped T-shirt", price: 130, oldPrice: 160, discount: 20, rating: 3.6 },
    { id: 7, name: "Vertical Striped Shirt", price: 212, oldPrice: 232, discount: 20, rating: 4.2 },
    { id: 8, name: "Courage Graphic T-shirt", price: 145, rating: 4.0 },
    { id: 9, name: "Loose Fit Bermuda Shorts", price: 80, rating: 3.5 },
];

const Main = () => {
    const [price, setPrice] = useState(200);
    const [color, setColor] = useState("");
    const [size, setSize] = useState("");
    const [style, setStyle] = useState("");

    const handlePriceChange = (event) => setPrice(event.target.value);
    const handleColorChange = (event) => setColor(event.target.value);
    const handleSizeChange = (event) => setSize(event.target.value);
    const handleStyleChange = (event) => setStyle(event.target.value);

    const filteredProducts = products.filter(product => 
        product.price <= price &&
        (!color || product.color === color) &&
        (!size || product.size === size) &&
        (!style || product.style === style)
    );

    return (
        <div className="container">
            <div className= "page-move">

            </div>
            <aside className="filters">
            <h3>Filters</h3>
                <label>Price: ${price}</label>
                <input type="range" min="40" max="200" value={price} onChange={handlePriceChange} />
                
                <label>Color</label>
                <select onChange={handleColorChange}>
                    <option value="">All</option>
                    <option value="white">White</option>
                    <option value="red">Red</option>
                    <option value="black">Black</option>
                    <option value="blue">Blue</option>
                    <option value="orange">Orange</option>
                    <option value="green">Green</option>
                </select>
                
                <label>Size</label>
                <select onChange={handleSizeChange}>
                    <option value="">All</option>
                    <option value="S">Small</option>
                    <option value="M">Medium</option>
                    <option value="L">Large</option>
                    <option value="XL">X-Large</option>
                </select>
                
                <label>Dress Style</label>
                <select onChange={handleStyleChange}>
                    <option value="">All</option>
                    <option value="casual">Casual</option>
                    <option value="formal">Formal</option>
                </select>
                
                <button>Apply Filter</button>
            </aside>
            <main className="product-grid">
                {products.map((product) => (
                    <div className="product-card" key={product.id}>
                        <img src={casual} alt={product.name} />
                        <h4>{product.name}</h4>
                        <p className="price">
                            ${product.price} {product.oldPrice && <span className="old-price">${product.oldPrice}</span>} 
                            {product.discount && <span className="discount">-{product.discount}%</span>}
                        </p>
                        <p className="rating">‚≠ê {product.rating}</p>
                    </div>
                ))}
            </main>
        </div>
    );
};

export default Main;
