import React from 'react';
import './css/Header.css'; // Đường dẫn tương đối đến tệp CSS

function Header() {
  return (
    <div className="promo-banner">
      <span className="promo-text">
        Sign up and get 20% off to your first order.<a href="./" className="promo-link">Sign Up Now</a>{' '}
        
      </span>
      <button className="promo-close">&times;</button>
    </div>
  );
}

export default Header;
